mapper.select:
SELECT c.*, IFNULL(temp.expected_amount, 0) AS expected_amount, IFNULL(temp.actual_amount, 0) AS actual_amount FROM coupon c LEFT JOIN (SELECT t.coupon_id, SUM( t.expected_interest + t.default_interest - t.expected_fee ) AS expected_amount, SUM( t.actual_interest + t.default_interest - t.actual_fee ) AS actual_amount FROM user_coupon t GROUP BY t.coupon_id) temp ON c.id = temp.coupon_id where c.user_group != 'EXCHANGER'
and c.c.coupon_type IN ('NEWBIE_COUPON','INVEST_COUPON')
and c.c.coupon_type=#{couponType}
and c.coupon_source like '%#{couponSource}%'
and c.amount=#{amount}
ORDER BY created_time DESC LIMIT #{index},#{pageSize}

select count(*) from coupon where user_group != 'EXCHANGER'

      and c.c.coupon_type IN ('NEWBIE_COUPON','INVEST_COUPON')


      and c.c.coupon_type=#{couponType}


      and c.coupon_source like '%#{couponSource}%'


      and c.amount=#{amount}
