select IFNULL(sum(amount),0) from invest JOIN loan ON invest.loan_id = loan.id
and invest.loan_id = #{loanId}
and invest.login_name = #{loginName}
and invest.transfer_invest_id IS NULL
and loan.product_type != 'EXPERIENCE'
and invest.status = 'SUCCESS'

SELECT invest.id AS invest_id, loan.id AS loan_id, loan.name AS loan_name, loan.periods AS loan_periods, invest.login_name AS investor_login_name, (SELECT province FROM user WHERE login_name = invest.login_name) AS province, (SELECT city FROM user WHERE login_name = invest.login_name) AS city, (SELECT identity_number FROM user WHERE user.login_name = invest.login_name) AS identity_number, (SELECT user_name FROM user WHERE user.login_name = invest.login_name) AS investor_username, (SELECT mobile FROM user WHERE login_name = invest.login_name) AS investor_mobile, (SELECT GROUP_CONCAT(role ORDER BY role SEPARATOR ', ') FROM user_role WHERE login_name = invest.login_name) AS investor_roles, (SELECT referrer FROM user WHERE login_name = invest.login_name) AS referrer_login_name, (SELECT user_name FROM user WHERE user.login_name = (SELECT referrer FROM user WHERE login_name = invest.login_name)) AS referrer_username, (SELECT mobile FROM user WHERE login_name = (SELECT referrer FROM user WHERE login_name = invest.login_name)) AS referrer_mobile, (SELECT GROUP_CONCAT(role ORDER BY role SEPARATOR ', ') FROM user_role WHERE login_name = (SELECT referrer FROM user WHERE login_name = invest.login_name)) AS referrer_roles, invest.channel AS channel, invest.source AS source, invest.invest_time AS invest_time, invest.is_auto_invest AS auto_invest, invest.amount AS invest_amount, (SELECT coupon.id FROM coupon join user_coupon on user_coupon.coupon_id = coupon.id
and coupon.coupon_type = #{preferenceType}
WHERE user_coupon.invest_id = invest.id limit 0, 1) AS coupon_id, (SELECT extra_rate FROM invest_extra_rate WHERE invest_extra_rate.invest_id = invest.id) AS extra_rate, (SELECT actual_interest FROM invest_extra_rate WHERE invest_extra_rate.invest_id = invest.id) AS extra_actual_interest, invest.status AS invest_status, invest.transfer_status as transfer_status, invest.is_overdue_transfer as is_overdue_transfer, (select invest_repay.status from invest_repay where invest_repay.invest_id =invest.id and invest_repay.period = loan.periods) AS last_period_repay_status FROM invest join loan on invest.loan_id = loan.id
invest.transfer_invest_id IS NULL
AND loan.product_type <> 'EXPERIENCE'
AND loan.product_type = #{productType}
AND invest.loan_id = #{loanId}
AND invest.login_name = #{investorLoginName}
AND invest.status = #{investStatus}
AND invest.channel = #{channel}
AND invest.source = #{source}
AND EXISTS (SELECT 1 FROM user_role WHERE user_role.login_name = invest.login_name AND user_role.role = #{role})
AND invest.invest_time >= #{startTime}
AND invest.invest_time <= #{endTime}
AND EXISTS (SELECT 1 FROM user_coupon join coupon on coupon.coupon_type = #{preferenceType} and user_coupon.coupon_id = coupon.id WHERE user_coupon.invest_id = invest.id)
AND EXISTS (SELECT 1 FROM invest_extra_rate WHERE invest_extra_rate.invest_id = invest.id)
and invest.transfer_status not in ('NONTRANSFERABLE') and invest.is_overdue_transfer = 0
and invest.is_overdue_transfer = 1
and invest.transfer_status = 'NONTRANSFERABLE' and invest.is_overdue_transfer = 0
ORDER BY CASE invest.trading_time WHEN NULL THEN invest.created_time ELSE invest.trading_time END DESC, invest.id ASC limit #{index}, #{pageSize}

select invest.*
from invest
join loan on invest.loan_id = loan.id

    and invest.login_name = #{loginName}

        and invest.created_time > #{startTime}


        and invest.created_time < #{endTime}


        AND (loan.status = #{loanStatus}
        OR (loan.product_type = 'EXPERIENCE' and exists(select * from invest_repay ir where
        ir.status=#{loanStatus} and ir.invest_id = invest.id and ir.period = 1)))



        AND loan.product_type != 'EXPERIENCE'


order by case when invest.trading_time is null then invest.created_time else invest.trading_time end desc,
invest.id asc
limit #{index}, #{pageSize}

