        AND EXISTS (SELECT 1 FROM invest_extra_rate WHERE invest_extra_rate.invest_id = invest.id)


        and invest.transfer_status not in ('NONTRANSFERABLE') and invest.is_overdue_transfer = 0


        and invest.is_overdue_transfer = 1


        and invest.transfer_status = 'NONTRANSFERABLE' and invest.is_overdue_transfer = 0

