select invest.* from invest join loan on invest.loan_id = loan.id where invest.login_name = #{loginName}
and (loan.status = #{loanStatus} OR ((#{loanStatus} = 'REPAYING' AND loan.product_type = 'EXPERIENCE' AND TO_DAYS(NOW()) - TO_DAYS(invest_time) < loan.duration) OR (#{loanStatus} = 'COMPLETE' AND loan.product_type = 'EXPERIENCE' AND TO_DAYS(NOW()) - TO_DAYS(invest_time) >= loan.duration)))
and (loan.status in ('REPAYING','OVERDUE') OR ((#{loanStatus} = 'REPAYING' AND loan.product_type = 'EXPERIENCE' AND TO_DAYS(NOW()) - TO_DAYS(invest_time) < loan.duration) OR (#{loanStatus} = 'COMPLETE' AND loan.product_type = 'EXPERIENCE' AND TO_DAYS(NOW()) - TO_DAYS(invest_time) >= loan.duration)))
and loan.status in ('RAISING','RECHECK') and loan.product_type != 'EXPERIENCE'
and invest.`status` = 'SUCCESS' and invest.`transfer_invest_id` is null and invest.`transfer_status` != 'SUCCESS' order by case when invest.trading_time is null then invest.created_time else invest.trading_time end desc, invest.id asc limit #{index}, #{pageSize}

