SELECT IFNULL(SUM(amount), 0) FROM invest WHERE status = 'SUCCESS' AND login_name = #{loginName} AND transfer_invest_id IS NULL
and trading_time between #{startTime} and #{endTime}
AND loan_id IN ( SELECT l.id FROM loan l JOIN loan_details d ON l.id = d.loan_id WHERE d.activity = 1 AND d.activity_desc = #{activityDesc} )

select ifnull(SUM(count),0)
from(
select if(FLOOR(ifnull(sum(amount), 0) /#{baseAmount})> 20, 20, FLOOR(sum(amount) /#{baseAmount})) as count
from invest
join loan on invest.loan_id = loan.id
where invest.status= 'SUCCESS'
and invest.`login_name`= #{loginName}
and invest.transfer_invest_id IS NULL
and loan.product_type != 'EXPERIENCE'

    and invest.trading_time between #{startTime} and #{endTime}

group by date_format(`trading_time`, '%Y-%m-%d')) drawCount;

SELECT temp.login_name,temp.amount,temp.trading_time,user.mobile FROM (SELECT login_name, SUM(amount) AS amount, MAX(trading_time) AS trading_time FROM invest i JOIN loan on i.loan_id = loan.id WHERE i.loan_id = #{loanId} and i.loan_id != 1 and loan.activity_type != 'NEWBIE'
AND loan.fundraising_start_time >= #{activityStartTime}
AND loan.fundraising_start_time <= #{activityEndTime}
AND i.status = 'SUCCESS' AND i.transfer_status != 'SUCCESS' GROUP BY i.login_name) temp join user on temp.login_name = user.login_name ORDER BY amount desc,trading_time
LIMIT #{limit}

SELECT temp.login_name,temp.amount,temp.trading_time,temp.loan_desc,user.mobile,user.user_name FROM (SELECT login_name, loan_details.activity_desc AS loan_desc, SUM(amount) AS amount, MAX(trading_time) AS trading_time FROM invest i JOIN loan on i.loan_id = loan.id JOIN loan_details on loan.id = loan_details.loan_id WHERE i.loan_id != 1 AND loan.activity_type != 'NEWBIE' AND loan_details.activity_desc in
#{activityDesc}
AND loan.fundraising_start_time BETWEEN #{startTime} AND #{endTime} AND i.status = 'SUCCESS' AND i.transfer_invest_id IS NULL GROUP BY loan_details.activity_desc, i.login_name) temp join user on temp.login_name = user.login_name

SELECT temp.login_name, temp.sumAmount, temp.product_type, user.mobile, user.user_name FROM (SELECT i.login_name, SUM(amount) AS sumAmount, loan.product_type FROM invest i JOIN loan on i.loan_id = loan.id JOIN loan_details on loan.id = loan_details.loan_id WHERE loan.activity_type != 'NEWBIE' AND i.status = 'SUCCESS' AND i.transfer_invest_id IS NULL AND loan.product_type <> 'EXPERIENCE' AND i.trading_time BETWEEN #{startTime} AND #{endTime}
and loan_details.activity_desc = #{activityDesc}
GROUP BY loan.product_type, i.login_name) temp join user on temp.login_name = user.login_name

SELECT invest.* FROM invest JOIN loan ON invest.loan_id = loan.id JOIN loan_details ON invest.loan_id = loan_details.loan_id WHERE invest.status = 'SUCCESS' AND invest.transfer_invest_id IS NULL AND loan.activity_type!='NEWBIE' AND loan.product_type != '_30' AND loan.id!='1' AND (loan_details.activity_desc IS NULL OR loan_details.activity_desc !='0元购') AND invest.trading_time BETWEEN #{startTime} AND #{endTime}
and invest.loan_id = #{loanId}
ORDER BY trading_time ASC
