mapper.select:
SELECT temp.* FROM loan temp left join loan_details on temp.id = loan_details.loan_id WHERE 1 = 1 and temp.status not in ('WAITING_VERIFY','CANCEL') and (loan_details.activity_desc <>'0元购' or loan_details.activity_desc is null or temp.product_type = 'EXPERIENCE')
and temp.name LIKE CONCAT(CONCAT('%', #{name}),'%')
and temp.status = #{status}
and temp.status != 'WAITING_VERIFY' and temp.status != 'CANCEL'
and (temp.base_rate + temp.activity_rate) >= #{rateStart}
and (temp.base_rate + temp.activity_rate) > #{rateStart}
and (temp.base_rate + temp.activity_rate) <= #{rateEnd}
and temp.duration BETWEEN #{durationStart} and #{durationEnd}
ORDER BY CASE temp.status WHEN 'RAISING' THEN '1' WHEN 'PREHEAT' THEN '2' ELSE 3 END, CASE WHEN temp.activity_type = 'NEWBIE' and temp.status in ('RAISING','PREHEAT') THEN '1' ELSE 2 END, CASE WHEN temp.status in ('RAISING','PREHEAT') THEN duration END, CASE WHEN temp.status in ('RAISING','PREHEAT','RECHECK') THEN verify_time END DESC, CASE WHEN temp.status in ('RAISING','PREHEAT','RECHECK') THEN created_time END DESC, CASE WHEN temp.status in ('REPAYING','OVERDUE','COMPLETE') THEN recheck_time END DESC limit #{index} , 10

select count(*) from (
select t.* from loan t left join loan_details on t.id = loan_details.loan_id
where 1=1
and (loan_details.activity_desc <>'0元购' or loan_details.activity_desc is null or t.product_type =
'EXPERIENCE')

    and t.name LIKE CONCAT(CONCAT('%', #{name}),'%')


    and t.status = #{status}


    and t.status != 'WAITING_VERIFY' and t.status != 'CANCEL'



         and (t.base_rate + t.activity_rate) >=  #{rateStart} 


         and (t.base_rate + t.activity_rate) >  #{rateStart} 




     and (t.base_rate + t.activity_rate) <=  #{rateEnd} 


    and t.duration BETWEEN #{durationStart} and #{durationEnd}

) temp

SELECT temp.* FROM loan temp left JOIN loan_details on temp.id = loan_details.loan_id WHERE 1 = 1 and (loan_details.activity_desc <>'0元购' or loan_details.activity_desc is null or temp.product_type = 'EXPERIENCE') and temp.status not in ('WAITING_VERIFY','CANCEL')
and temp.product_type = #{productType}
and temp.product_type != #{noContainProductType}
and temp.status = #{status}
and (temp.base_rate + temp.activity_rate) >= #{rateStart}
and (temp.base_rate + temp.activity_rate) > #{rateStart}
and (temp.base_rate + temp.activity_rate) <= #{rateEnd}
ORDER BY CASE temp.status WHEN 'RAISING' THEN '1' WHEN 'PREHEAT' THEN '2' ELSE 3 END, CASE WHEN temp.activity_type = 'NEWBIE' and temp.status in ('RAISING','PREHEAT') THEN '1' ELSE 2 END, CASE WHEN temp.status in ('RAISING','PREHEAT') THEN duration END, CASE WHEN temp.status in ('RAISING','PREHEAT','RECHECK') THEN verify_time END DESC, CASE WHEN temp.status in ('RAISING','PREHEAT','RECHECK') THEN created_time END DESC, CASE WHEN temp.status in ('REPAYING','OVERDUE','COMPLETE') THEN recheck_time END DESC limit #{index} , #{pageSize}

select count(*) from (
select * from loan t
where 1=1

    and t.product_type = #{productType}


    and t.status = #{status}


    and t.status != 'WAITING_VERIFY'



         and (t.base_rate + t.activity_rate) >=  #{rateStart} 


         and (t.base_rate + t.activity_rate) >  #{rateStart} 




     and (t.base_rate + t.activity_rate) <=  #{rateEnd} 


) temp

select count(1) from loan
where agent_login_name = #{agentLoginName} and status in ('REPAYING', 'OVERDUE')
      and
      (
          (select min(loan_repay.repay_date) from loan_repay where loan.id=loan_repay.loan_id and loan_repay.status in ('REPAYING', 'WAIT_PAY', 'OVERDUE')) between #{startTime} and #{endTime}
      or
          (select min(loan_repay.repay_date) from loan_repay where loan.id=loan_repay.loan_id and loan_repay.status = 'COMPLETE') between #{startTime} and #{endTime}
      )

select count(1) from loan
where agent_login_name = #{agentLoginName} and status = 'COMPLETE'
and (select max(loan_repay.actual_repay_date) from loan_repay where loan.id=loan_repay.loan_id) between #{startTime} and #{endTime}

select * from loan
where agent_login_name = #{agentLoginName} and status = 'CANCEL' and recheck_time between #{startTime} and #{endTime}
order by recheck_time desc, id asc
limit #{index}, #{pageSize}

select * from loan t


        t.status = #{status}


        and t.id = #{loanId}


        and t.name like CONCAT(CONCAT('%', #{loanName}),'%')


        and t.created_time >= #{startTime}


         and t.created_time <= #{endTime} 


order by t.created_time desc

    limit #{currentPageNo}, #{pageSize}

select count(*) from loan t


        t.status = #{status}


        and t.id = #{loanId}


        and t.name like CONCAT(CONCAT('%', #{loanName}),'%')


        and t.created_time >= #{startTime}


         and t.created_time <= #{endTime}

SELECT * FROM (SELECT l.`id` as loan_id, l.`name`, l.`status` as loan_status, l.`periods`, l.`loan_amount`, l.`created_time`, l.`raising_complete_time`, l.`fundraising_start_time`, l.`first_invest_achievement_id`, l.last_invest_achievement_id, l.max_amount_achievement_id, (SELECT u.mobile FROM invest i join user u on i.login_name = u.login_name WHERE i.id = l.first_invest_achievement_id) AS first_invest_login_name, (SELECT i.amount FROM invest i WHERE i.id = l.first_invest_achievement_id) AS first_invest_amount, (SELECT u.mobile FROM invest i join user u on i.login_name = u.login_name WHERE i.id = l.last_invest_achievement_id) AS last_invest_login_name, (SELECT i.amount FROM invest i WHERE i.id = l.last_invest_achievement_id) AS last_invest_amount, (SELECT u.mobile FROM invest i join user u on i.login_name = u.login_name WHERE i.id = l.max_amount_achievement_id) AS max_amount_login_name, (SELECT i.amount FROM invest i WHERE i.id = l.max_amount_achievement_id) AS max_amount FROM loan l WHERE l.`status` IN ( 'RAISING', 'RECHECK', 'REPAYING', 'COMPLETE' )) temp
WHERE temp.max_amount_login_name = #{mobile} OR temp.last_invest_login_name = #{mobile} OR temp.first_invest_login_name = #{mobile}
ORDER BY temp.created_time DESC limit #{index}, #{pageSize}

SELECT COUNT(id) FROM loan
WHERE DATE_FORMAT(created_time,'%Y') = #{createdTime}

    and pledge_type = #{pledgeType}


    and pledge_type in ('ENTERPRISE_FACTORING', 'ENTERPRISE_BILL', 'ENTERPRISE_PLEDGE', 'ENTERPRISE_CREDIT')
