SELECT l.`name` AS loan_name, l.`periods` AS periods, n.investMobile AS invest_mobile, n.investAmount AS invest_amount, n.investTime AS invest_time, n.referrerMobile AS referrer_mobile, n.role_name AS role, n.level, n.rewardAmount AS reward_amount, n.status, n.rewardTime AS reward_time, n.investName AS invest_name, n.referrerName AS referrer_name, n.source FROM (SELECT temp.investMobile, temp.investAmount, temp.investTime, temp.loanId, temp.rewardAmount, temp.rewardTime, temp.referrerMobile, temp.role_name, temp.status, temp.investName, temp.referrerName, temp.source, rr.`level` FROM referrer_relation rr right JOIN (SELECT i.`login_name` AS investLoginName, i.`invest_mobile` AS investMobile, i.`amount` AS investAmount, i.`created_time` AS investTime, i.`loan_id` AS loanId, i.investName, i.source, irr.`amount` AS rewardAmount, irr.`created_time` AS rewardTime, irr.`referrer_login_name` AS referrerLoginName, irr.`referrer_mobile` AS referrerMobile, irr.`referrer_role` as role_name, irr.`status`, irr.referrerName FROM (SELECT a.*, b.`user_name` AS referrerName, referrer_u.`mobile` AS referrer_mobile FROM (invest_referrer_reward a LEFT JOIN user b ON a.`referrer_login_name` = b.`login_name`) LEFT JOIN user referrer_u ON a.`referrer_login_name` = referrer_u.login_name
referrer_u.`mobile` = #{referrerMobile}
AND a.`created_time` >= #{rewardStartTime}
AND a.`created_time` <= #{rewardEndTime}
AND a.`referrer_role` = #{role}
AND a.`referrer_role` not in ('SD_STAFF', 'ZC_STAFF')
AND a.`status` = #{referrerRewardStatus}
) irr JOIN (SELECT m.*, n.`user_name` AS investName, invest_u.`mobile` AS invest_mobile FROM (invest m LEFT JOIN user n ON m.`login_name` = n.`login_name`) LEFT JOIN user invest_u ON m.`login_name` = invest_u.`login_name`
invest_u.`mobile` = #{investMobile}
AND m.`created_time` >= #{investStartTime}
AND m.`created_time` <= #{investEndTime}
AND m.`source` = #{source}
AND m.`status` = 'SUCCESS'
) i ON irr.`invest_id` = i.`id`) temp ON rr.`login_name` = temp.investLoginName AND rr.`referrer_login_name` = temp.referrerLoginName
rr.`level` = #{level}
) n JOIN loan l ON n.loanId = l.`id` AND l.`status` IN ('REPAYING','OVERDUE','COMPLETE') ORDER BY n.rewardTime DESC LIMIT #{startLimit},#{endLimit}

SELECT COUNT(1) FROM (SELECT temp.loanId FROM referrer_relation rr right JOIN (SELECT i.`login_name` AS investLoginName, i.`loan_id` AS loanId, irr.`referrer_login_name` AS referrerLoginName FROM (SELECT a.*, b.`user_name` AS referrerName, referrer_u.`mobile` AS referrer_mobile FROM (invest_referrer_reward a LEFT JOIN user b ON a.`referrer_login_name` = b.`login_name`) LEFT JOIN user referrer_u ON a.`referrer_login_name` = referrer_u.login_name
referrer_u.`mobile` = #{referrerMobile}
AND a.`created_time` >= #{rewardStartTime}
AND a.`created_time` <= #{rewardEndTime}
AND a.`referrer_role` = #{role}
AND a.`referrer_role` not in ('SD_STAFF', 'ZC_STAFF')
AND a.`status` = #{referrerRewardStatus}
) irr JOIN (SELECT m.*, n.`user_name` AS investName, invest_u.`mobile` AS invest_mobile FROM (invest m LEFT JOIN user n ON m.`login_name` = n.`login_name`) LEFT JOIN user invest_u ON m.`login_name` = invest_u.`login_name`
invest_u.`mobile` = #{investMobile}
AND m.`created_time` >= #{investStartTime}
AND m.`created_time` <= #{investEndTime}
AND m.`source` = #{source}
AND m.`status` = 'SUCCESS'
) i ON irr.`invest_id` = i.`id`) temp ON rr.`login_name` = temp.investLoginName AND rr.`referrer_login_name` = temp.referrerLoginName
rr.`level` = #{level}
) n JOIN loan l ON n.loanId = l.`id` AND l.`status` IN ('REPAYING','OVERDUE','COMPLETE')

SELECT IFNULL(sum(investAmount),0) as invest_amount FROM (SELECT temp.investAmount, temp.loanId FROM referrer_relation rr right JOIN (SELECT i.`login_name` AS investLoginName, i.`amount` AS investAmount, i.`created_time` AS investTime, i.`loan_id` AS loanId, i.investName, irr.`amount` AS rewardAmount, irr.`created_time` AS rewardTime, irr.`referrer_login_name` AS referrerLoginName, irr.`referrer_role` as role_name, irr.`status`, irr.referrerName FROM (SELECT a.*, b.`user_name` AS referrerName, referrer_u.`mobile` AS referrer_mobile FROM (invest_referrer_reward a LEFT JOIN user b ON a.`referrer_login_name` = b.`login_name`) LEFT JOIN user referrer_u ON a.`referrer_login_name` = referrer_u.login_name
referrer_u.`mobile` = #{referrerMobile}
AND a.`created_time` >= #{rewardStartTime}
AND a.`created_time` <= #{rewardEndTime}
AND a.`referrer_role` = #{role}
AND a.`referrer_role` not in ('SD_STAFF', 'ZC_STAFF')
) irr JOIN (SELECT m.*, n.`user_name` AS investName, invest_u.`mobile` AS invest_mobile FROM (invest m LEFT JOIN user n ON m.`login_name` = n.`login_name`) LEFT JOIN user invest_u ON m.`login_name` = invest_u.`login_name`
invest_u.`mobile` = #{investMobile}
AND m.`created_time` >= #{investStartTime}
AND m.`created_time` <= #{investEndTime}
AND m.`source` = #{source}
AND m.`status` = 'SUCCESS'
) i ON irr.`invest_id` = i.`id`) temp ON rr.`login_name` = temp.investLoginName AND rr.`referrer_login_name` = temp.referrerLoginName
rr.`level` = #{level}
) n JOIN loan l ON n.loanId = l.`id` AND l.`status` IN ('REPAYING','OVERDUE','COMPLETE')

