rr.`level` = #{level}
) n JOIN loan l ON n.loanId = l.`id` AND l.`status` IN ('REPAYING','OVERDUE','COMPLETE')

SELECT
    rr.login_name,
    rr.level,
    u.register_time,
    u.mobile
FROM
    referrer_relation rr
LEFT JOIN user u
    on rr.login_name = u.login_name


         rr.level <= #{level} 


        and rr.referrer_login_name = #{referrerLoginName}


        and rr.login_name = #{loginName}


         and u.`register_time` >= #{referStartTime} 


         and u.`register_time` <= #{referEndTime} 


ORDER BY u.register_time DESC limit #{startLimit}, #{endLimit}

SELECT
    count(rr.referrer_login_name)
FROM
    referrer_relation rr
LEFT JOIN user u
    on rr.login_name = u.login_name


         rr.level <= #{level} 


        and rr.referrer_login_name = #{referrerLoginName}


        and rr.login_name = #{loginName}


         and u.`register_time` >= #{referStartTime} 


         and u.`register_time` <= #{referEndTime}

select i.loan_id AS loan_id, i.login_name AS invest_name, (select mobile from user where user.login_name = i.login_name) as invest_mobile, rr.level, l.name AS loan_name, i.amount AS invest_amount, l.periods, i.created_time AS invest_time, irr.amount AS reward_amount, irr.created_time AS reward_time, irr.status AS status, l.product_type AS product_type from invest_referrer_reward irr left join invest i on i.id = irr.invest_id left join loan l on l.id = i.loan_id left join referrer_relation rr on rr.login_name = i.login_name and rr.referrer_login_name = irr.referrer_login_name
irr.status != 'FORBIDDEN'
and irr.referrer_login_name = #{referrerLoginName}
and i.login_name=#{loginName}
and (rr.level <= #{level} or rr.level is null)
and i.`created_time` >= #{investStartTime}
and i.`created_time` <= #{investEndTime}
ORDER BY invest_time DESC limit #{startLimit}, #{endLimit}

select COUNT(1) from invest_referrer_reward irr left join invest i on i.id = irr.invest_id left join loan l on l.id = i.loan_id left join referrer_relation rr on rr.login_name = i.login_name and rr.referrer_login_name = irr.referrer_login_name
irr.referrer_login_name = #{referrerLoginName}
and i.login_name=#{loginName}
and (rr.level <= #{level} or rr.level is null)
and i.`created_time` >= #{investStartTime}
and i.`created_time` <= #{investEndTime}

select IFNULL(sum(irr.amount),0) from invest_referrer_reward irr left join invest i on i.id = irr.invest_id left join loan l on l.id = i.loan_id left join referrer_relation rr on rr.login_name = i.login_name and rr.referrer_login_name = irr.referrer_login_name
irr.referrer_login_name = #{referrerLoginName}
and i.login_name=#{loginName}
and (rr.level <= #{level} or rr.level is null)
and i.`created_time` >= #{investStartTime}
and i.`created_time` <= #{investEndTime}
and irr.status='SUCCESS'

select  IFNULL(sum(i.amount),0) AS invest_amount from referrer_relation rr left join invest i on rr.login_name = i.login_name


        rr.referrer_login_name = #{referrerLoginName}

    and i.status='SUCCESS'
