mapper.select:
select uc.* from user_coupon uc

    JOIN coupon c on uc.coupon_id = c.id and c.coupon_type in

        #{couponType}


where uc.login_name = #{loginName}

select uc.*, c.id as coupon_model_id, c.amount as amount, c.rate as rate, c.period as period, c.birthday_benefit as birthday_benefit, c.multiple as multiple, c.start_time as coupon_start_time, c.end_time as coupon_end_time, c.used_count as used_count, c.total_count as total_count, c.active as active, c.shared as shared, c.issued_count as issued_count, c.invest_lower_limit as invest_lower_limit, c.coupon_type as coupon_type, c.product_types as product_types, c.deleted as deleted, c.user_group as user_group, c.deadline as deadline, c.coupon_source as coupon_source, c.comment as comment from user_coupon uc JOIN coupon c on uc.coupon_id = c.id
and c.coupon_type in
#{couponType}
where uc.login_name = #{loginName}

SELECT
t.*
FROM
user_coupon t
JOIN user u
ON t.`login_name` = u.`login_name`

    AND u.`login_name` = #{loginName}


    AND u.`mobile` = #{mobile}


    AND t.`created_time` = #{createdTime}


    AND u.`register_time` >= #{registerStartTime}


     AND u.`register_time` <= #{registerEndTime} 

where t.coupon_id = #{couponId}

    AND t.`used_time` >= #{investStartTime}


     AND t.`used_time` <= #{investEndTime} 


    and t.status = 'SUCCESS'


    and (t.status != 'SUCCESS' or t.status is null)

order by t.used_time desc
limit #{index}, #{pageSize}

SELECT
ifnull(count(*),0)
FROM
user_coupon t
JOIN user u
ON t.`login_name` = u.`login_name`

    AND u.`login_name` = #{loginName}


    AND u.`mobile` = #{mobile}


    AND u.`register_time` >= #{registerStartTime}


     AND u.`register_time` <= #{registerEndTime} 

where t.coupon_id = #{couponId}

    AND t.`used_time` >= #{investStartTime}


     AND t.`used_time` <= #{investEndTime} 


    and t.status = 'SUCCESS'


    and (t.status != 'SUCCESS' or t.status is null)

select * from user_coupon
where login_name = #{loginName}

    and coupon_id = #{couponId}

SELECT IFNULL(SUM(c.amount), 0) FROM user_coupon u JOIN coupon c ON u.coupon_id = c.id WHERE u.login_name = #{loginName}
AND u.coupon_id in
#{couponId}
