mapper.select:
SELECT id, title, creator_login_name, checker_login_name, author, section, source, carousel, thumb, show_picture,
content, created_time, updated_time, deleted,timing_time
FROM `aa`.`licaiquan_article` WHERE id = #{id}

mapper.update:
UPDATE `aa`.`licaiquan_article` SET title = #{title}, creator_login_name = #{creatorLoginName},
checker_login_name = #{checkerLoginName}, author = #{author}, section = #{section}, source = #{source},
carousel = #{carousel}, thumb = #{thumb}, show_picture = #{showPicture}, content = #{content},
created_time = #{createdTime}, updated_time = #{updatedTime},timing_time = #{timingTime}
WHERE id = #{id}

mapper.select:
SELECT id, title, creator_login_name, checker_login_name, author, section, source, carousel, thumb, show_picture, content, created_time, updated_time, deleted,timing_time FROM `aa`.`licaiquan_article` WHERE id >= #{index} AND deleted = 0
AND title LIKE CONCAT(CONCAT('%', #{title}), '%')
AND section = #{section}
ORDER BY id,created_time DESC LIMIT #{pageSize}

SELECT id, title, creator_login_name, checker_login_name, author, section, source, carousel, thumb, show_picture, content, created_time, updated_time, deleted,timing_time FROM `aa`.`licaiquan_article` WHERE deleted = FALSE AND (now() >= timing_time or timing_time is null)
AND section = #{section}
AND section != 'KNOWLEDGE'
ORDER BY updated_time DESC LIMIT #{index},#{pageSize}

SELECT count(1) FROM `aa`.`licaiquan_article` WHERE deleted = FALSE AND (now() >= timing_time or timing_time is null)
AND section = #{section}
AND section != 'KNOWLEDGE'
