mapper.select:
select a.loan_count as `name`, count(*) as `value`, '续投用户' as `group` from ( select count(distinct i.loan_id) as loan_count from invest i
inner join `user` u on i.login_name = u.login_name
where i.status='SUCCESS' and i.created_time between #{startTime} and #{endTime}
and u.province = #{province}
group by i.login_name ) a group by a.loan_count;

select temp.login_name, a.user_name, u.mobile, IFNULL((select 1 from user_role ur where ur.login_name = temp.login_name and ur.role='STAFF'),0) as is_staff, u.referrer, ra.user_name as referrer_user_name, IFNULL((select 1 from user_role ur where ur.login_name = u.referrer and ur.role='STAFF'),0) as is_referrer_staff, temp.total_amount, temp.last_invest_time, temp.loan_count from ( select i.login_name, IFNULL(SUM(i.amount),0) as total_amount, MAX(i.created_time) as last_invest_time, count(distinct i.loan_id) as loan_count from invest i
inner join `user` uu on i.login_name = uu.login_name
where i.status='SUCCESS' AND i.created_time between #{startTime} and #{endTime}
and uu.province = #{province}
group by i.login_name ) temp LEFT JOIN `user` u on temp.login_name = u.login_name LEFT JOIN `account` a on temp.login_name = a.login_name LEFT JOIN account ra on u.referrer = ra.login_name WHERE
temp.loan_count >= #{loanCount}
temp.loan_count = #{loanCount}
ORDER BY temp.total_amount DESC LIMIT #{startIndex}, #{pageSize}

select
    count(temp.login_name)
from (
    select
        i.login_name,
        count(distinct i.loan_id) as loan_count
    from invest i

            inner join `user` uu
            on i.login_name = uu.login_name

    where
        i.status='SUCCESS'
        AND i.created_time between #{startTime} and #{endTime}

            and uu.province = #{province}

    group by i.login_name
    ) temp
WHERE

     temp.loan_count >= #{loanCount} 


    temp.loan_count = #{loanCount}

select
    IFNULL(SUM(temp.total_amount),0) as sum_amount
from (
    select
        i.login_name,
        IFNULL(SUM(i.amount),0) as total_amount,
        count(distinct i.loan_id) as loan_count
    from invest i

            inner join `user` uu
            on i.login_name = uu.login_name

    where
        i.status='SUCCESS'
        AND i.created_time between #{startTime} and #{endTime}

            and uu.province = #{province}

    group by i.login_name
) temp
WHERE

         temp.loan_count >= #{loanCount} 


        temp.loan_count = #{loanCount}

select
    if(age<20,'20 岁以下',if(age<35,'20~35 岁',if(age<50,'35~50 岁','50 岁以上'))) as `name`,
    count(1) as `value`,

         #{province} 
         "全部" 
     as `group`
from
    (
        select date_format(now(),'%Y')-if(length(identity_number)=18,substring(identity_number,7,4),1900+substring(identity_number,7,2)) as age
        from
            account a

        join user u
            on a.login_name=u.login_name
            and u.province=#{province}

        where
            length(identity_number) in (15,18)
            and a.register_time between #{startTime} and #{endTime}

            and exists
                (
                    select 1 from invest i where i.login_name=a.login_name
                )

    ) temp
group by `name`;
