mapper.select:
select a.loan_count as `name`, count(*) as `value`, '续投用户' as `group` from ( select count(distinct i.loan_id) as loan_count from invest i
inner join `user` u on i.login_name = u.login_name
where i.status='SUCCESS' and i.created_time between #{startTime} and #{endTime}
and u.province = #{province}
group by i.login_name ) a group by a.loan_count;

select a.loan_count as `name`, count(*) as `value`, '续投用户' as `group` from ( select count(distinct id) as loan_count from invest i
inner join `user` u on i.login_name = u.login_name
where i.status='SUCCESS' and i.created_time between #{startTime} and #{endTime}
and u.province = #{province}
group by i.login_name ) a group by a.loan_count;

select temp.login_name, u.user_name, u.mobile, IFNULL((select 1 from user_role ur where ur.login_name = temp.login_name and ur.role in ('SD_STAFF', 'ZC_STAFF')),0) as is_staff, u.referrer, (select referrer.user_name from user referrer where referrer.login_name = u.referrer) as referrer_user_name, IFNULL((select 1 from user_role ur where ur.login_name = u.referrer and ur.role in ('SD_STAFF', 'ZC_STAFF')),0) as is_referrer_staff, temp.total_amount, temp.last_invest_time, temp.loan_count from ( select i.login_name, IFNULL(SUM(i.amount),0) as total_amount, MAX(i.created_time) as last_invest_time, count(distinct i.loan_id) as loan_count from invest i
inner join `user` uu on i.login_name = uu.login_name
where i.status='SUCCESS' AND i.created_time between #{startTime} and #{endTime}
and uu.province = #{province}
group by i.login_name ) temp LEFT JOIN `user` u on temp.login_name = u.login_name WHERE
temp.last_invest_time >= #{loanCount}
temp.loan_count = #{loanCount}
ORDER BY temp.total_amount DESC LIMIT #{startIndex}, #{pageSize}

select temp.login_name, u.user_name, u.mobile, IFNULL((select 1 from user_role ur where ur.login_name = temp.login_name and ur.role in ('SD_STAFF', 'ZC_STAFF')),0) as is_staff, u.referrer, (select referrer.user_name from user referrer where referrer.login_name = u.referrer) as referrer_user_name, IFNULL((select 1 from user_role ur where ur.login_name = u.referrer and ur.role in ('SD_STAFF', 'ZC_STAFF')),0) as is_referrer_staff, temp.total_amount, temp.last_invest_time, temp.loan_count from ( select i.login_name, IFNULL(SUM(i.amount),0) as total_amount, MAX(i.created_time) as last_invest_time, count(distinct i.id) as loan_count from invest i
inner join `user` uu on i.login_name = uu.login_name
where i.status='SUCCESS' AND i.created_time between #{startTime} and #{endTime}
and uu.province = #{province}
group by i.login_name ) temp LEFT JOIN `user` u on temp.login_name = u.login_name WHERE
temp.loan_count >= #{investCount}
temp.loan_count = #{investCount}
ORDER BY temp.total_amount DESC LIMIT #{startIndex}, #{pageSize}

select
    count(temp.login_name)
from (
    select
        i.login_name,
        count(distinct i.loan_id) as loan_count
    from invest i

            inner join `user` uu
            on i.login_name = uu.login_name

    where
        i.status='SUCCESS'
        AND i.created_time between #{startTime} and #{endTime}

            and uu.province = #{province}

    group by i.login_name
    ) temp
WHERE

     temp.loan_count >= #{loanCount} 


    temp.loan_count = #{loanCount}

