SELECT user.id as id, user.login_name as login_name, user.mobile as mobile, user.email as email, user.source as source, user.channel as channel, user.province as province, user.city as city, user.register_time as register_time, user.status as status, user.user_name as user_name, user.identity_number as identity_number, (select mobile from user referrer where referrer.login_name = user.referrer) AS referrer_mobile FROM user
AND user.`identity_number` = #{identityNumber}
AND user.`login_name` = #{loginName}
AND EXISTS (select 1 from user referrer where referrer.login_name = user.referrer and referrer.mobile = #{referrerMobile})
AND user.`mobile` = #{mobile}
ORDER BY user.`register_time` DESC

SELECT user.id as id, user.login_name as login_name, user.mobile as mobile, user.province as province, user.city as city, IF(exists (select 1 from user_role where user_role.login_name = user.login_name and user_role.role in ('SD_STAFF', 'ZC_STAFF')), TRUE, FALSE) as is_staff, user.register_time as register_time, user.status as status, user.user_name as user_name, account.balance as balance, (select max(`user_bill`.`created_time`) from `user_bill` where `user_bill`.`login_name` = `user`.`login_name`) as last_bill_time FROM `user` JOIN `account` ON `user`.`login_name` = `account`.`login_name` WHERE NOT EXISTS (SELECT 1 FROM `user_role` WHERE `user`.`login_name` = `user_role`.`login_name` AND `user_role`.`role` = 'LOANER')
AND `user`.`mobile` = #{mobile}
AND `account`.`balance` >= #{balanceMin}
AND `account`.`balance` <= #{balanceMax}
ORDER BY `account`.`balance` DESC
limit #{startLimit}, #{endLimit}

SELECT IFNULL(SUM(`account`.`balance`), 0)
FROM `user`
JOIN `account`
ON `user`.`login_name` = `account`.`login_name`
WHERE NOT EXISTS (SELECT 1 FROM `user_role`  WHERE `user`.`login_name` = `user_role`.`login_name` AND `user_role`.`role` = 'LOANER')

    AND `user`.`mobile` = #{mobile}


    AND `account`.`balance` >= #{balanceMin}


    AND `account`.`balance` <= #{balanceMax}

SELECT IFNULL(count(1), 0)
FROM `user`
JOIN `account`
ON `user`.`login_name` = `account`.`login_name`
WHERE NOT EXISTS (SELECT 1 FROM `user_role`  WHERE `user`.`login_name` = `user_role`.`login_name` AND `user_role`.`role` = 'LOANER')

    AND `user`.`mobile` = #{mobile}


    AND `account`.`balance` >= #{balanceMin}


    AND `account`.`balance` <= #{balanceMax}

select
    count(1)
from user u


        AND u.mobile = #{mobile}


        AND exists (select 1 from user_role where u.login_name = user_role.login_name and user_role.role = #{role})


        AND u.register_time >= #{registerTimeStart}


        AND u.register_time < #{registerTimeEnd}


        AND u.identity_number is not null


        AND u.identity_number is null


        AND EXISTS (select 1 from invest i where i.login_name=u.login_name and i.status='SUCCESS' and loan_id != 1 order by i.created_time asc limit 1)


        AND NOT EXISTS (select 1 from invest i where i.login_name=u.login_name and i.status='SUCCESS' and loan_id != 1 order by i.created_time asc limit 1)


        AND (select sum(amount) from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1) >= #{totalInvestAmountStart}


        AND (select sum(amount) from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1) <= #{totalInvestAmountEnd}


        AND (select sum(amount) from withdraw w where w.login_name=u.login_name and w.status='SUCCESS') >= #{totalWithdrawAmountStart}


        AND (select sum(amount) from withdraw w where w.login_name=u.login_name and w.status='SUCCESS') <= #{totalWithdrawAmountEnd}


        AND (select balance from account a where a.login_name=u.login_name) >= #{userBalanceStart}


        AND (select balance from account a where a.login_name=u.login_name) <= #{userBalanceEnd}


        AND (select count(1) from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1) >= #{investCountStart}


        AND (select count(1) from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1) <= #{investCountEnd}


        AND (select count(distinct l.id) from loan l, invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id = l.id and l.id != 1) >= #{loanCountStart}


        AND (select count(distinct l.id) from loan l, invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id = l.id and l.id != 1) <= #{loanCountEnd}


        AND DATEDIFF((select created_time from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1 order by i.created_time asc limit 1),
        u.register_time) >= #{transformPeriodStart}


        AND DATEDIFF((select created_time from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1 order by i.created_time asc limit 1),
        u.register_time) <= #{transformPeriodEnd}


        AND DATEDIFF((select created_time from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1 order by i.created_time asc limit 1,1),
        (select created_time from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1 order by i.created_time asc limit 1)) >= #{invest1st2ndTimingStart}


        AND DATEDIFF((select created_time from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1 order by i.created_time asc limit 1,1),
        (select created_time from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1 order by i.created_time asc limit 1)) <= #{invest1st2ndTimingEnd}


        AND DATEDIFF((select created_time from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1 order by i.created_time asc limit 2,1),
        (select created_time from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1 order by i.created_time asc limit 1)) >= #{invest1st3ndTimingStart}


        AND DATEDIFF((select created_time from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1 order by i.created_time asc limit 2,1),
        (select created_time from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1 order by i.created_time asc limit 1)) <= #{invest1st3ndTimingEnd}


        AND (select created_time from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1 order by i.created_time desc limit 1) > #{lastInvestTimeStart}


        AND (select created_time from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1 order by i.created_time desc limit 1) < #{lastInvestTimeEnd}


        AND (select max(ir.repay_date) from invest_repay ir join invest i on i.id=ir.invest_id where i.login_name=u.login_name) > #{lastRepayTimeStart}


        AND (select max(ir.repay_date) from invest_repay ir join invest i on i.id=ir.invest_id where i.login_name=u.login_name) < #{lastRepayTimeEnd}


        AND (select max(created_time) from withdraw where login_name=u.login_name and status='SUCCESS') > #{lastWithdrawTimeStart}


        AND (select max(created_time) from withdraw where login_name=u.login_name and status='SUCCESS') < #{lastWithdrawTimeEnd}


        AND (IFNULL((select sum(ir.corpus+ir.expected_interest+ir.default_interest-ir.expected_fee-ir.default_fee+ir.overdue_interest-ir.overdue_fee) from invest_repay ir, invest i where i.login_name=u.login_name and i.id=ir.invest_id and ir.status='REPAYING' and i.status='SUCCESS'),0) +
        IFNULL((select sum(cr.expected_interest-cr.expected_fee) from coupon_repay cr, invest i where i.login_name=u.login_name and i.id=cr.invest_id and cr.status='REPAYING' and i.status='SUCCESS'),0) +
        IFNULL((select sum(ier.expected_interest-ier.expected_fee) from invest_extra_rate ier, invest i where i.login_name=u.login_name and i.id=ier.invest_id and ier.status='REPAYING' and i.status='SUCCESS'),0)) >= #{repayingAmountStart}


        AND (IFNULL((select sum(ir.corpus+ir.expected_interest+ir.default_interest-ir.expected_fee-ir.default_fee+ir.overdue_interest-ir.overdue_fee) from invest_repay ir, invest i where i.login_name=u.login_name and i.id=ir.invest_id and ir.status='REPAYING' and i.status='SUCCESS'),0) +
        IFNULL((select sum(cr.expected_interest-cr.expected_fee) from coupon_repay cr, invest i where i.login_name=u.login_name and i.id=cr.invest_id and cr.status='REPAYING' and i.status='SUCCESS'),0) +
        IFNULL((select sum(ier.expected_interest-ier.expected_fee) from invest_extra_rate ier, invest i where i.login_name=u.login_name and i.id=ier.invest_id and ier.status='REPAYING' and i.status='SUCCESS'),0)) <= #{repayingAmountEnd}


        AND u.last_login_time >= #{lastLoginTimeStart}


        AND u.last_login_time < #{lastLoginTimeEnd}


        AND u.last_login_source = #{lastLoginSource}

select
    u.id,
    u.user_name,
    u.mobile,
    u.channel,
    u.register_time,
    DATEDIFF(IFNULL((select created_time from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1 order by i.created_time asc limit 1),now()),
        u.register_time) as no_invest_period,
    EXISTS (select 1 from invest i where i.login_name=u.login_name and i.status='SUCCESS' and loan_id != 1 order by i.created_time asc limit 1) as invested,
    (select sum(amount) from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1) as total_invest_amount,
    (select sum(amount) from withdraw w where w.login_name=u.login_name and w.status='SUCCESS') as total_withdraw_amount,
    (select balance from account a where a.login_name=u.login_name) as user_balance,
    (select count(1) from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1) as invest_count,
    (select count(distinct l.id) from loan l, invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id = l.id and l.id != 1) as loan_count,
    DATEDIFF((select created_time from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1 order by i.created_time asc limit 1),
    u.register_time) as transform_period,
    DATEDIFF((select created_time from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1 order by i.created_time asc limit 1,1),
    (select created_time from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1 order by i.created_time asc limit 1)) as invest_1st_2nd_timing,
    DATEDIFF((select created_time from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1 order by i.created_time asc limit 2,1),
    (select created_time from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1 order by i.created_time asc limit 1)) as invest_1st_3rd_timing,
    (select created_time from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1 order by i.created_time desc limit 1) as last_invest_time,
    (select max(ir.repay_date) from invest_repay ir join invest i on i.id=ir.invest_id where i.login_name=u.login_name) as last_repay_time,
    (select max(created_time) from withdraw where login_name=u.login_name and status='SUCCESS') as last_withdraw_time,
    (IFNULL((select sum(ir.corpus+ir.expected_interest+ir.default_interest-ir.expected_fee-ir.default_fee+ir.overdue_interest-ir.overdue_fee) from invest_repay ir, invest i where i.login_name=u.login_name and i.id=ir.invest_id and ir.status='REPAYING' and i.status='SUCCESS'),0) +
    IFNULL((select sum(cr.expected_interest-cr.expected_fee) from coupon_repay cr, invest i where i.login_name=u.login_name and i.id=cr.invest_id and cr.status='REPAYING' and i.status='SUCCESS'),0) +
    IFNULL((select sum(ier.expected_interest-ier.expected_fee) from invest_extra_rate ier, invest i where i.login_name=u.login_name and i.id=ier.invest_id and ier.status='REPAYING' and i.status='SUCCESS'),0)) as total_repaying_amount,
    u.last_login_time,
    u.last_login_source
from user u


        AND u.mobile = #{mobile}


        AND exists (select 1 from user_role where u.login_name = user_role.login_name and user_role.role = #{role})


        AND u.register_time >= #{registerTimeStart}


        AND u.register_time < #{registerTimeEnd}


        AND u.identity_number is not null


        AND u.identity_number is null


        AND EXISTS (select 1 from invest i where i.login_name=u.login_name and i.status='SUCCESS' and loan_id != 1 order by i.created_time asc limit 1)


        AND NOT EXISTS (select 1 from invest i where i.login_name=u.login_name and i.status='SUCCESS' and loan_id != 1 order by i.created_time asc limit 1)


        AND (select sum(amount) from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1) >= #{totalInvestAmountStart}


        AND (select sum(amount) from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1) <= #{totalInvestAmountEnd}


        AND (select sum(amount) from withdraw w where w.login_name=u.login_name and w.status='SUCCESS') >= #{totalWithdrawAmountStart}


        AND (select sum(amount) from withdraw w where w.login_name=u.login_name and w.status='SUCCESS') <= #{totalWithdrawAmountEnd}


        AND (select balance from account a where a.login_name=u.login_name) >= #{userBalanceStart}


        AND (select balance from account a where a.login_name=u.login_name) <= #{userBalanceEnd}


        AND (select count(1) from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1) >= #{investCountStart}


        AND (select count(1) from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1) <= #{investCountEnd}


        AND (select count(distinct l.id) from loan l, invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id = l.id and l.id != 1) >= #{loanCountStart}


        AND (select count(distinct l.id) from loan l, invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id = l.id and l.id != 1) <= #{loanCountEnd}


        AND DATEDIFF((select created_time from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1 order by i.created_time asc limit 1),
        u.register_time) >= #{transformPeriodStart}


        AND DATEDIFF((select created_time from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1 order by i.created_time asc limit 1),
        u.register_time) <= #{transformPeriodEnd}


        AND DATEDIFF((select created_time from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1 order by i.created_time asc limit 1,1),
        (select created_time from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1 order by i.created_time asc limit 1)) >= #{invest1st2ndTimingStart}


        AND DATEDIFF((select created_time from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1 order by i.created_time asc limit 1,1),
        (select created_time from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1 order by i.created_time asc limit 1)) <= #{invest1st2ndTimingEnd}


        AND DATEDIFF((select created_time from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1 order by i.created_time asc limit 2,1),
        (select created_time from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1 order by i.created_time asc limit 1)) >= #{invest1st3ndTimingStart}


        AND DATEDIFF((select created_time from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1 order by i.created_time asc limit 2,1),
        (select created_time from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1 order by i.created_time asc limit 1)) <= #{invest1st3ndTimingEnd}


        AND (select created_time from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1 order by i.created_time desc limit 1) > #{lastInvestTimeStart}


        AND (select created_time from invest i where i.login_name=u.login_name and i.status='SUCCESS' and i.loan_id != 1 order by i.created_time desc limit 1) < #{lastInvestTimeEnd}


