        AND (IFNULL((select sum(ir.corpus+ir.expected_interest+ir.default_interest-ir.expected_fee-ir.default_fee+ir.overdue_interest-ir.overdue_fee) from invest_repay ir, invest i where i.login_name=u.login_name and i.id=ir.invest_id and ir.status='REPAYING' and i.status='SUCCESS'),0) +
