limit #{index}, #{pageSize}

SELECT login_name, mobile, user_name, register_time,
(select count(1) > 0 from invest where loan_id = 1 and invest.login_name = user.login_name and status = "SUCCESS") as use_experience_coupon,
(select invest_time from invest where loan_id = 1 and invest.login_name = user.login_name and status = "SUCCESS" order by invest_time desc limit 1) as experience_time,
(select count(1) from invest where login_name = user.login_name and loan_id != 1 and status = "SUCCESS") as invest_count,
(select sum(amount) from invest where login_name = user.login_name and status = "SUCCESS") as invest_sum,
(select trading_time from invest where login_name = user.login_name and loan_id != 1 and status = "SUCCESS" order by trading_time limit 1) as first_invest_time,
(select amount from invest where login_name = user.login_name and loan_id != 1 and status = "SUCCESS" order by trading_time limit 1) as first_invest_amount,
(select trading_time from invest where login_name = user.login_name and loan_id != 1 and status = "SUCCESS" order by trading_time limit 1, 1) as second_invest_time,
(select amount from invest where login_name = user.login_name and loan_id != 1 and status = "SUCCESS" order by trading_time limit 1, 1) as second_invest_amount
FROM aa.user


        AND login_name = #{loginName}


        AND mobile = #{mobile}


        AND register_time >= #{registerStartTime}


        AND register_time <= #{registerEndTime}



            AND (select count(1) from invest where loan_id = 1 and invest.login_name = user.login_name and status = "SUCCESS") > 0


            AND (select count(1) from invest where loan_id = 1 and invest.login_name = user.login_name and status = "SUCCESS") = 0



        AND exists (select 1 from invest where loan_id = 1 and invest.login_name = user.login_name and status = "SUCCESS" and invest_time >= #{experienceStartTime})


        AND exists (select 1 from invest where loan_id = 1 and invest.login_name = user.login_name and status = "SUCCESS" and invest_time <= #{experienceEndTime})


        AND (select count(1) from invest where login_name = user.login_name AND loan_id != 1 and status = "SUCCESS") >= #{investCountLowLimit}


        AND (select count(1) from invest where login_name = user.login_name AND loan_id != 1 and status = "SUCCESS") <= #{investCountHighLimit}


        AND (select sum(amount) from invest where login_name = user.login_name and status = "SUCCESS") >= #{investSumLowLimit}


        AND (select sum(amount) from invest where login_name = user.login_name and status = "SUCCESS") <= #{investSumHighLimit}


        AND (select trading_time from invest where login_name = user.login_name and loan_id != 1 and status = "SUCCESS"order by trading_time limit 1 ) >= #{firstInvestStartTime}


        AND (select trading_time from invest where login_name = user.login_name and loan_id != 1 and status = "SUCCESS" order by trading_time limit 1 ) <= #{firstInvestEndTime}


        AND (select trading_time from invest where login_name = user.login_name and loan_id != 1 and status = "SUCCESS" order by trading_time limit 1, 1 ) >= #{secondInvestStartTime}


        AND (select trading_time from invest where login_name = user.login_name and loan_id != 1 and status = "SUCCESS" order by trading_time limit 1, 1 ) <= #{secondInvestEndTime}


ORDER BY register_time DESC
LIMIT #{index}, #{pageSize}

