        AND exists (select 1 from invest where loan_id = 1 and invest.login_name = user.login_name and status = "SUCCESS" and invest_time >= #{experienceStartTime})


        AND exists (select 1 from invest where loan_id = 1 and invest.login_name = user.login_name and status = "SUCCESS" and invest_time <= #{experienceEndTime})


        AND (select count(1) from invest where login_name = user.login_name AND loan_id != 1 and status = "SUCCESS") >= #{investCountLowLimit}


        AND (select count(1) from invest where login_name = user.login_name AND loan_id != 1 and status = "SUCCESS") <= #{investCountHighLimit}


        AND (select sum(amount) from invest where login_name = user.login_name and status = "SUCCESS") >= #{investSumLowLimit}


        AND (select sum(amount) from invest where login_name = user.login_name and status = "SUCCESS") <= #{investSumHighLimit}


        AND (select trading_time from invest where login_name = user.login_name and loan_id != 1 and status = "SUCCESS"order by trading_time limit 1 ) >= #{firstInvestStartTime}


        AND (select trading_time from invest where login_name = user.login_name and loan_id != 1 and status = "SUCCESS" order by trading_time limit 1 ) <= #{firstInvestEndTime}


        AND (select trading_time from invest where login_name = user.login_name and loan_id != 1 and status = "SUCCESS" order by trading_time limit 1, 1 ) >= #{secondInvestStartTime}


        AND (select trading_time from invest where login_name = user.login_name and loan_id != 1 and status = "SUCCESS" order by trading_time limit 1, 1 ) <= #{secondInvestEndTime}

select u.*, e.experience_balance
from experience_account e
inner join user u on u.login_name = e.login_name

    e.experience_balance > 0

        and u.mobile = #{mobile}


        and e.experience_balance > #{balanceMin}


        and e.experience_balance <= #{balanceMax}


order by e.experience_balance DESC
limit #{index}, #{pageSize}

