SELECT u.id as id, u.login_name as login_name, u.mobile as mobile, u.email as email, u.source as source, u.channel as channel, u.province as province, u.city as city, u.register_time as register_time, u.status as status, a.user_name as user_name, a.identity_number as identity_number, (select mobile from user referrer where referrer.login_name = u.referrer) AS referrer_mobile, IF(exists (select 1 from auto_invest_plan au where u.login_name = au.login_name and enabled is TRUE), "0", "1") as auto_invest_status, IF(exists (select 1 from user_role referrer_role where referrer_role.login_name = u.referrer and referrer_role.role = 'STAFF'), TRUE, FALSE) as is_referrer_staff, IF(exists (select 1 from user_role where user_role.login_name = u.login_name and user_role.role = 'STAFF'), TRUE, FALSE) as is_staff, IF(exists (select 1 from bank_card where bank_card.login_name = u.login_name and bank_card.status = 'PASSED'), TRUE, FALSE) as is_bind_bank_card from
user u
(SELECT j.* FROM user j WHERE NOT EXISTS( SELECT 1 FROM (SELECT ff.login_name FROM (SELECT k.login_name FROM user_role o RIGHT JOIN referrer_relation k ON o.login_name = k.referrer_login_name AND k.level = 1 WHERE o.role = 'STAFF' UNION SELECT h.login_name FROM user_role h WHERE h.role IN ('STAFF' , 'AGENT')) ff) gg WHERE gg.login_name = j.login_name) and EXISTS(select 1 from invest i where j.login_name = i.login_name) ) u
(select j.* from user j JOIN (select k.login_name from user_role o RIGHT join referrer_relation k on o.login_name = k.referrer_login_name and k.level = 1 where o.role = 'STAFF') gg on j.login_name = gg.login_name) u
user u join user_role r on u.login_name = r.login_name
left join account a on u.login_name = a.login_name
and r.role = #{roleStage}
and u.email like '${email}%'
and u.mobile like '${mobile}%'
and u.login_name = #{loginName}
and u.register_time >= #{beginTime}
and u.register_time <= #{endTime}
and exists (select 1 from user referrer where u.referrer = referrer.login_name and referrer.mobile = #{referrerMobile})
and u.channel = #{channel}
and u.source = #{source}
order by u.register_time desc limit #{index}, #{pageSize}

