SELECT user.id as id, user.login_name as login_name, user.mobile as mobile, user.email as email, user.source as source, user.channel as channel, user.province as province, user.city as city, user.register_time as register_time, user.status as status, account.user_name as user_name, account.identity_number as identity_number, (select mobile from user referrer where referrer.login_name = user.referrer) AS referrer_mobile FROM user LEFT JOIN account ON user.login_name = account.login_name
AND account.`identity_number` = #{identityNumber}
AND user.`login_name` = #{loginName}
AND EXISTS (select 1 from user referrer where referrer.login_name = user.referrer and referrer.mobile = #{referrerMobile})
AND user.`mobile` = #{mobile}
ORDER BY user.`register_time` DESC

SELECT user.id as id, user.login_name as login_name, user.mobile as mobile, user.province as province, user.city as city, IF(exists (select 1 from user_role where user_role.login_name = user.login_name and user_role.role = 'STAFF'), TRUE, FALSE) as is_staff, user.register_time as register_time, user.status as status, account.user_name as user_name, account.balance as balance, (select max(`user_bill`.`created_time`) from `user_bill` where `user_bill`.`login_name` = `user`.`login_name`) as last_bill_time FROM `user` JOIN `account` ON `user`.`login_name` = `account`.`login_name` WHERE NOT EXISTS (SELECT 1 FROM `user_role` WHERE `user`.`login_name` = `user_role`.`login_name` AND `user_role`.`role` = 'LOANER')
AND `user`.`mobile` = #{mobile}
AND `account`.`balance` >= #{balanceMin}
AND `account`.`balance` <= #{balanceMax}
ORDER BY `account`.`balance` DESC
limit #{startLimit}, #{endLimit}

SELECT IFNULL(SUM(`account`.`balance`), 0)
FROM `user`
JOIN `account`
ON `user`.`login_name` = `account`.`login_name`
WHERE NOT EXISTS (SELECT 1 FROM `user_role`  WHERE `user`.`login_name` = `user_role`.`login_name` AND `user_role`.`role` = 'LOANER')

    AND `user`.`mobile` = #{mobile}


    AND `account`.`balance` >= #{balanceMin}


    AND `account`.`balance` <= #{balanceMax}

SELECT IFNULL(count(1), 0)
FROM `user`
JOIN `account`
ON `user`.`login_name` = `account`.`login_name`
WHERE NOT EXISTS (SELECT 1 FROM `user_role`  WHERE `user`.`login_name` = `user_role`.`login_name` AND `user_role`.`role` = 'LOANER')

    AND `user`.`mobile` = #{mobile}


    AND `account`.`balance` >= #{balanceMin}


    AND `account`.`balance` <= #{balanceMax}
