mapper.select:
select count(1) from membership_purchase
where status = 'SUCCESS'

    and mobile = #{mobile}


    and duration = #{duration}


    and source = #{source}


    and created_time >= #{startTime}


    and created_time <= #{endTime}

select * from membership_purchase
where status = 'SUCCESS'

    and mobile = #{mobile}


    and duration = #{duration}


    and source = #{source}


    and created_time >= #{startTime}


    and created_time <= #{endTime}

order by created_time desc, id asc
limit #{index}, #{pageSize}
