mapper.select:
select uc.* from user_coupon uc

    JOIN coupon c on uc.coupon_id = c.id and c.coupon_type in

        #{couponType}


where uc.login_name = #{loginName}

select count(1) from user_coupon uc join coupon c on uc.coupon_id=c.id and c.deleted is FALSE and c.coupon_type in
#{couponType}
join loan l on l.id=uc.loan_id join invest i on i.id = uc.invest_id where uc.status='SUCCESS' and uc.login_name = #{loginName}

select uc.id as id, l.id as loan_id, l.name as loan_name, c.coupon_type as coupon_type, c.rate as rate, i.amount as invest_amount, c.amount as coupon_amount, uc.used_time as used_time, uc.expected_interest - uc.expected_fee as expected_income from user_coupon uc join coupon c on uc.coupon_id=c.id and c.deleted is FALSE and c.coupon_type in
#{couponType}
join loan l on l.id=uc.loan_id join invest i on i.id = uc.invest_id where uc.status='SUCCESS' and uc.login_name = #{loginName} ORDER BY used_time DESC limit #{index}, #{pageSize}
