mapper.select:
select COUNT(*) from jpush_alert where is_automatic = #{isAutomatic}

    and push_type = #{pushType}


    and push_source = #{pushSource}


    and push_user_type = #{pushUserType}


    and status = #{pushStatus}


    and expect_push_time >= #{startTime}


     and expect_push_time <= #{endTime}

select * from jpush_alert where is_automatic = #{isAutomatic}


    and push_type = #{pushType}


    and push_source = #{pushSource}


    and push_user_type = #{pushUserType}


    and status = #{pushStatus}


    and expect_push_time >= #{startTime}


     and expect_push_time <= #{endTime} 


ORDER BY created_time DESC
LIMIT #{index},#{pageSize}

mapper.update:
