jump_to = #{jumpTo},jump_to_link=#{jumpToLink},expect_push_time=#{expectPushTime},updated_time=#{updatedTime},updated_by=#{updatedBy},auditor=#{auditor},
android_target_num=#{androidTargetNum},ios_target_num=#{iosTargetNum},android_arrive_num=#{androidArriveNum},ios_arrive_num=#{iosArriveNum} where id = #{id}
