mapper.select:
SELECT
a.*
FROM user u
JOIN account a ON u.login_name = a.login_name
WHERE a.point > 0

    AND a.login_name = #{loginName}


    AND a.user_name = #{userName}



    AND u.mobile = #{mobile}

ORDER BY a.point DESC
limit #{startLimit}, #{endLimit}

SELECT
COUNT(1)
FROM user u
JOIN account a ON u.login_name = a.login_name
WHERE a.point > 0

    AND a.login_name = #{loginName}


    AND a.user_name = #{userName}


    AND u.mobile = #{mobile}

mapper.update:
UPDATE account SET
point = point - #{exchangePoint}
WHERE login_name = #{loginName}
