mapper.select:
SELECT * FROM `aa`.`activity`


        AND activated_time >= #{activatedStartTime}


        AND activated_time <= #{activatedEndTime}


        AND status = #{activityStatus}


        and source like CONCAT(CONCAT('%', #{source}), '%')

SELECT * FROM `aa`.`activity`


        AND activated_time >= #{startTime}


        AND activated_time <= #{endTime}


        AND status = #{activityStatus}


        and source = #{source}


order by activated_time desc
