mapper.select:
SELECT * FROM `aa`.`activity`


        AND activated_time >= #{startTime}


        AND activated_time <= #{endTime}


        AND status = 'APPROVED' AND expired_time > now()


        AND expired_time < now()


        AND status = #{activityStatus}


        AND source like CONCAT('%', #{source}, '%')


order by activated_time desc

SELECT * FROM `aa`.`activity` WHERE expired_time > #{expiredTime} AND status = 'APPROVED'
AND source like CONCAT('%', #{source}, '%')
ORDER BY activated_time DESC LIMIT #{index}, #{pageSize}

SELECT * FROM `aa`.`activity`


        AND expired_time >= #{beginExpiredTime}


        AND expired_time <= #{endExpiredTime}


        AND status = #{activityStatus}


        AND source like CONCAT('%', #{source}, '%')


        OR long_term = #{longTerm}


order by seq ASC ,updated_time DESC
LIMIT #{index}, #{pageSize}
