select invest.*, loan.id as loan_id, loan.name as loan_name, loan.status as loan_status, loan.type as loan_type, loan.periods as loan_periods, loan.base_rate as loan_base_rate, loan.activity_rate as loan_activity_rate, a.user_name as investor_user_name, u.mobile as investor_mobile, a.identity_number as identity_number, u.province as province, u.city as city, u.referrer as referrer_login_name, ra.user_name as referrer_user_name, ru.mobile as referrer_mobile, (select group_concat(role order by role separator ", ") from user_role where login_name = invest.login_name) as roles, (select group_concat(role order by role separator ", ") from user_role where login_name = u.referrer) as referrerRoles, (select min(repay_date) from invest_repay where invest_repay.invest_id = invest.id and invest_repay.status = 'REPAYING') as next_repay_date, (select invest_repay.expected_interest + invest_repay.corpus - invest_repay.expected_fee from invest_repay where invest_repay.invest_id = invest.id and invest_repay.status = 'REPAYING' and invest_repay.period = (select min(invest_repay.period) from invest_repay where invest_repay.invest_id = invest.id and invest_repay.status = 'REPAYING') ) as next_repay_amount from invest inner join loan on invest.loan_id = loan.id inner join user u on u.login_name = invest.login_name
and u.mobile = #{investorMobile}
left join account a on a.login_name = u.login_name left join user ru on ru.login_name = u.referrer left join account ra on ra.login_name = u.referrer
invest.transfer_invest_id is null
and loan.id = #{loanId}
and loan.status = #{loanStatus}
and invest.status = #{investStatus}
and invest.channel = #{channel}
and invest.source = #{source}
and exists (select 1 from user_role where user_role.login_name=invest.login_name and user_role.role= #{role} )
and invest.created_time between #{startTime} and #{endTime}
order by case invest.trading_time when null then invest.created_time else invest.trading_time end desc, invest.id asc limit #{index}, #{pageSize}

