SELECT invest.loan_id, invest.id as invest_id, invest.amount , invest.created_time, invest.transfer_status, loan.name as loan_name, loan.base_rate, loan.activity_rate, ( select min(ir.repay_date) from invest_repay ir where ir.invest_id = invest.id and ir.status in ('REPAYING') ) as next_repay_date,
(select ir.expected_interest + ir.corpus - ir.expected_fee from invest_repay ir where ir.invest_id = invest.id and ir.status in ('REPAYING') and ir.period = (select min(ir.period) from invest_repay ir where ir.invest_id = invest.id and ir.status in ('REPAYING')) ) as next_repay_amount FROM invest INNER JOIN loan ON invest.loan_id = loan.id
WHERE invest.login_name = #{loginName} AND loan.status = 'REPAYING' AND invest.status = 'SUCCESS' AND invest.transfer_status = 'TRANSFERABLE' AND invest.transfer_invest_id IS NULL AND EXISTS (SELECT * FROM `loan_repay` lr where lr.`loan_id` = loan.`id` AND DATE(NOW()) >= DATE(loan.`recheck_time`) AND DATE(lr.`repay_date`) >=DATE(NOW()) AND DATEDIFF(DATE_FORMAT(lr.repay_date, '%Y-%m-%d'), DATE_FORMAT(NOW(), '%Y-%m-%d')) >= ( SELECT days_limit FROM transfer_rule ) ) AND (NOT EXISTS (SELECT * FROM `transfer_application` ta1 where ta1.`transfer_invest_id` = invest.id) or DATEDIFF(DATE_FORMAT(NOW(), '%Y-%m-%d'),DATE_FORMAT((select max(ta2.application_time) from `transfer_application` ta2 where ta2.`transfer_invest_id` = invest.`id` and ta2.status = 'CANCEL'),'%Y-%m-%d')) <> 0) ORDER BY invest.created_time DESC limit #{index}, #{pageSize}

