mapper.select:
select 
from invest
where id = #{id}

select 
from invest
where login_name = #{loginName}
order by created_time desc
limit 1;

select 
from invest
where loan_id = #{loanId}
and status = '${status}'
order by created_time desc
limit #{index}, #{pageSize}

mapper.update:
update invest set status = 'FAIL'
where loan_id = #{loanId}
and status = 'WAIT_PAY'

mapper.select:
select count(*)
from invest
where loan_id = #{loanId}
and status = 'WAIT_PAY'
and created_time >= #{afterTime}

select IFNULL(sum(amount),0)
from invest


        and loan_id = #{loanId}


        and login_name = #{loginName}


and status = 'SUCCESS'

select invest.*, loan.id as loan_id, loan.name as loan_name, loan.status as loan_status, loan.type as loan_type, loan.periods as loan_periods, loan.base_rate as loan_base_rate, loan.activity_rate as loan_activity_rate, a.user_name as investor_user_name, u.mobile as investor_mobile, a.identity_number as identity_number, u.province as province, u.city as city, u.referrer as referrer_login_name, ra.user_name as referrer_user_name, ru.mobile as referrer_mobile, (select group_concat(role order by role separator ", ") from user_role where login_name = invest.login_name) as roles, (select group_concat(role order by role separator ", ") from user_role where login_name = u.referrer) as referrerRoles, (select min(repay_date) from invest_repay where invest_repay.invest_id = invest.id and invest_repay.status = 'REPAYING') as next_repay_date, (select invest_repay.expected_interest + invest_repay.corpus - invest_repay.expected_fee from invest_repay where invest_repay.invest_id = invest.id and invest_repay.status = 'REPAYING' and invest_repay.period = (select min(invest_repay.period) from invest_repay where invest_repay.invest_id = invest.id and invest_repay.status = 'REPAYING') ) as next_repay_amount from invest inner join loan on invest.loan_id = loan.id inner join user u on u.login_name = invest.login_name left join account a on a.login_name = u.login_name left join user ru on ru.login_name = u.referrer left join account ra on ra.login_name = u.referrer
and loan.id = #{loanId}
and invest.login_name = #{investorLoginName}
and loan.status = #{loanStatus}
and invest.status = #{investStatus}
and invest.channel = #{channel}
and invest.source = #{source}
and exists (select 1 from user_role where user_role.login_name=invest.login_name and user_role.role= #{role} )
and invest.created_time between #{startTime} and #{endTime}
order by invest.created_time desc, invest.id asc limit #{index}, #{pageSize}

select count(1)
from invest join loan on invest.loan_id = loan.id


        and loan.id = #{loanId}


        and invest.login_name = #{investorLoginName}


        and loan.status = #{loanStatus}


        and invest.status = #{investStatus}


        and invest.channel = #{channel}


        and invest.source = #{source}


        and exists (select 1 from user_role where user_role.login_name=invest.login_name and user_role.role= #{role} )


        and invest.created_time between #{startTime} and #{endTime}

select IFNULL(sum(invest.amount), 0) as amount
from invest join loan on invest.loan_id = loan.id


        and loan.id = #{loanId}


        and invest.login_name = #{investorLoginName}


        and loan.status = #{loanStatus}


        and invest.status = #{investStatus}


        and invest.channel = #{channel}


        and invest.source = #{source}


        and exists (select 1 from user_role where user_role.login_name=invest.login_name and user_role.role= #{role} )


        and invest.created_time between #{startTime} and #{endTime}

select count(*)
from invest
where loan_id = #{loanId}
and invest.login_name = #{loginName}
and source = 'AUTO'
