mapper.select:
select invest_repay.*
from invest_repay join invest on invest_repay.invest_id = invest.id
where invest_id = #{investId}
and invest.login_name = #{loginName}
order by period asc

select ir.id, ir.invest_id, ir.corpus, ir.default_interest, ir.expected_interest, ir.actual_interest, ir.expected_fee, ir.actual_fee, ir.repay_amount, ir.period, ir.repay_date, ir.actual_repay_date, ir.status, ir.created_time from invest_repay ir inner join invest on invest.id = ir.invest_id where invest.login_name = #{loginName}
and ir.status = 'COMPLETE' order by actual_repay_date desc
and ir.status <> 'COMPLETE' order by repay_date asc
limit #{rowLimit} offset #{rowIndex}

select count(*) from invest_repay ir inner join invest on invest.id = ir.invest_id where invest.login_name = #{loginName}
and ir.status = 'COMPLETE'
and ir.status <> 'COMPLETE'
