mapper.select:
SELECT id, title, creator_login_name, checker_login_name, author, section, source, carousel, thumb, show_picture,
content, created_time, updated_time, deleted
FROM `aa`.`licaiquan_article` WHERE id = #{id}

mapper.update:
UPDATE `aa`.`licaiquan_article` SET title = #{title}, creator_login_name = #{creator}, checker_login_name = #{checker},
author = #{author}, section = #{articleSection}, source = #{source}, carousel = #{carousel}, thumb = #{thumb},
show_picture = #{showPicture}, content = #{content}, created_time = #{createTime}, updated_time = #{updateTime}
WHERE id = #{id}

mapper.select:
SELECT id, title, creator_login_name, checker_login_name, author, section, source, carousel, thumb, show_picture,
content, created_time, updated_time, deleted
FROM `aa`.`licaiquan_article`
WHERE id >= #{startId} AND deleted = FALSE

    AND title = #{title}


    AND section = #{section}

ORDER BY created_time DESC
LIMIT #{size}

SELECT id, title, creator_login_name, checker_login_name, author, section, source, carousel, thumb, show_picture,
content, created_time, updated_time, deleted
FROM `aa`.`licaiquan_article`
WHERE id >= #{startId} AND deleted = TRUE

