mapper.select:
select * from loan t


        t.status = #{status}


        and t.id = #{loanId}


        and t.name like CONCAT(CONCAT('%', #{loanName}),'%')


        and t.created_time >= #{startTime}


        and t.created_time <= #{endTime}


order by t.created_time desc

    limit (#{currentPageNo} - 1) * 10 , 10

select count(*) from loan t


        t.status = #{status}


        and t.id = #{loanId}


        and t.name like CONCAT(CONCAT('%', #{loanName}),'%')


        and t.created_time >= #{startTime}


        and t.created_time <= #{endTime}

select * from loan t


        t.activity_type = #{activityType}


        and t.status = #{status}


        and t.periods >= #{periodsStart}


        and t.periods <= #{periodsEnd}


        and (t.basic_rate + t.activity_rate) >= #{rateStart}


        and (t.basic_rate + t.activity_rate) <= #{rateEnd}

    order by case t.status when 'RAISING' then '1' when 'PREHEAT' then '2' when 'RECHECK' then '3' when 'REPAYING' then '4' when 'COMPLETE' then '5' else '6' end,t.created_time desc

        limit (#{currentPageNo} - 1) * 10 , 10

select count(*) from loan t


        t.activity_type = #{activityType}


        and t.status = #{status}


        and t.periods >= #{periodsStart}


        and t.periods <= #{periodsEnd}


        and (t.basic_rate + t.activity_rate) >= #{rateStart}


        and (t.basic_rate + t.activity_rate) <= #{rateEnd}

select count(1) from loan
where loaner_login_name = #{loanerLoginName} and status = 'REPAYING'
and (select min(loan_repay.repay_date) from loan_repay where loan.id=loan_repay.loan_id and loan_repay.status = 'REPAYING') between #{startTime} and #{endTime}

select count(1) from loan
where loaner_login_name = #{loanerLoginName} and status = 'COMPLETE'
and (select max(loan_repay.repay_date) from loan_repay where loan.id=loan_repay.loan_id) between #{startTime} and #{endTime}

select * from loan
where loaner_login_name = #{loanerLoginName} and status = 'CANCEL' and recheck_time between #{startTime} and #{endTime}
order by recheck_time desc, id asc
limit #{index}, #{pageSize}
