mapper.select:
select r.*, a.user_name, u.mobile,
IFNULL((select 1 from user_role ur where ur.login_name = r.login_name and ur.role='STAFF'),0) as is_staff
from recharge r
JOIN `user` u on r.login_name = u.login_name
JOIN account a on r.login_name = a.login_name


        r.id = #{rechargeId}


        and r.login_name like '${loginName}%'


        and r.created_time >= #{startTime}


        and r.created_time <= #{endTime}


        and r.status  = #{status}


        and r.source  = #{source}


        and r.channel  = #{channel}


order by r.created_time desc
limit #{index}, #{pageSize}

select count(1) from recharge


        id = #{rechargeId}


        and login_name like '${loginName}%'


        and created_time >= #{startTime}


        and created_time <= #{endTime}


        and status  = #{status}


        and source  = #{source}


        and channel  = #{channel}

select IFNULL(sum(re.amount),0) as amount from recharge re

    join user_role ur on re.login_name = ur.login_name and ur.role= #{role}



        id = #{rechargeId}


        and re.login_name like '${loginName}%'


        and re.created_time >= #{startTime}


        and re.created_time <= #{endTime}


        and status  = #{status}


        and source  = #{source}


        and channel  = #{channel}
