mapper.select:
select * from recharge


        id = #{rechargeId}


        and login_name like '${loginName}%'


        and created_time >= #{startTime}


        and created_time <= #{endTime}


        and status  = #{status}


        and source  = #{source}


        and channel  = #{channel}


order by created_time desc
limit #{index}, #{pageSize}

select count(1) from recharge


        id = #{rechargeId}


        and login_name like '${loginName}%'


        and created_time >= #{startTime}


        and created_time <= #{endTime}


        and status  = #{status}


        and source  = #{source}


        and channel  = #{channel}

select IFNULL(sum(amount),0) as amount from recharge


        id = #{rechargeId}


        and login_name like '${loginName}%'


        and created_time >= #{startTime}


        and created_time <= #{endTime}


        and status  = #{status}


        and source  = #{source}


        and channel  = #{channel}
