SELECT l.`name` AS loan_name, l.`periods` AS periods, n.investLoginName AS invest_login_name, n.investAmount AS invest_amount, n.investTime AS invest_time, n.referrerLoginName AS referrer_login_name, n.role_name AS role, n.level, n.rewardAmount AS reward_amount, n.status, n.rewardTime AS reward_time, n.investName AS invest_name, n.referrerName AS referrer_name, n.source FROM (SELECT temp.investLoginName, temp.investAmount, temp.investTime, temp.loanId, temp.rewardAmount, temp.rewardTime, temp.referrerLoginName, temp.role_name, temp.status, temp.investName, temp.referrerName, temp.source, rr.`level` FROM referrer_relation rr right JOIN (SELECT i.`login_name` AS investLoginName, i.`amount` AS investAmount, i.`created_time` AS investTime, i.`loan_id` AS loanId, i.investName, i.source, irr.`amount` AS rewardAmount, irr.`created_time` AS rewardTime, irr.`referrer_login_name` AS referrerLoginName, irr.`referrer_role` as role_name, irr.`status`, irr.referrerName FROM (SELECT a.*, b.`user_name` AS referrerName FROM invest_referrer_reward a LEFT JOIN account b ON a.`referrer_login_name` = b.`login_name`
a.`referrer_login_name` = #{referrerLoginName}
AND a.`created_time` >= #{rewardStartTime}
AND a.`created_time` <= #{rewardEndTime}
AND a.`referrer_role` = #{role}
AND a.`referrer_role` != 'STAFF'
) irr JOIN (SELECT m.*, n.`user_name` AS investName FROM invest m LEFT JOIN account n ON m.`login_name` = n.`login_name`
m.`login_name` = #{investLoginName}
AND m.`created_time` >= #{investStartTime}
AND m.`created_time` <= #{investEndTime}
AND m.`source` = #{source}
AND m.`status` = 'SUCCESS') i ON irr.`invest_id` = i.`id`) temp ON rr.`login_name` = temp.investLoginName AND rr.`referrer_login_name` = temp.referrerLoginName
rr.`level` = #{level}
) n JOIN loan l ON n.loanId = l.`id` AND l.`status` IN ('REPAYING','OVERDUE','COMPLETE') ORDER BY n.rewardTime DESC LIMIT #{startLimit},#{endLimit}

SELECT COUNT(*) FROM (SELECT temp.investLoginName, temp.investAmount, temp.investTime, temp.loanId, temp.rewardAmount, temp.rewardTime, temp.referrerLoginName, temp.role_name, temp.status, temp.investName, temp.referrerName, rr.`level` FROM referrer_relation rr right JOIN (SELECT i.`login_name` AS investLoginName, i.`amount` AS investAmount, i.`created_time` AS investTime, i.`loan_id` AS loanId, i.investName, irr.`amount` AS rewardAmount, irr.`created_time` AS rewardTime, irr.`referrer_login_name` AS referrerLoginName, irr.`referrer_role` as role_name, irr.`status`, irr.referrerName FROM (SELECT a.*, b.`user_name` AS referrerName FROM invest_referrer_reward a LEFT JOIN account b ON a.`referrer_login_name` = b.`login_name`
a.`referrer_login_name` = #{referrerLoginName}
AND a.`created_time` >= #{rewardStartTime}
AND a.`created_time` <= #{rewardEndTime}
