select ub.*,
a.user_name,
u.mobile,
IFNULL((select 1 from user_role ur where ur.login_name = ub.login_name and ur.role = 'STAFF'),0) as is_staff
from user_bill ub
LEFT JOIN `user` u on ub.login_name = u.login_name
LEFT JOIN account a on ub.login_name = a.login_name


        and ub.business_type = #{userBillBusinessType}


