select u.*, a.*, IF(au.login_name is null, "0", "1") as auto_invest_status from
user u
(SELECT j.* FROM user j WHERE NOT EXISTS( SELECT 1 FROM (SELECT ff.login_name FROM (SELECT k.login_name FROM user_role o RIGHT JOIN referrer_relation k ON o.login_name = k.referrer_login_name AND k.level = 1 WHERE o.role = 'STAFF' UNION SELECT h.login_name FROM user_role h WHERE h.role IN ('STAFF' , 'AGENT')) ff) gg WHERE gg.login_name = j.login_name) and EXISTS(select 1 from invest i where j.login_name = i.login_name) ) u
(select j.* from user j JOIN (select k.login_name from user_role o RIGHT join referrer_relation k on o.login_name = k.referrer_login_name and k.level = 1 where o.role = 'STAFF') gg on j.login_name = gg.login_name) u
user u join user_role r on u.login_name = r.login_name
left join account a on u.login_name = a.login_name left join auto_invest_plan au on u.login_name = au.login_name and enabled=1
and r.role = #{roleStage}
and u.email like '${email}%'
and u.mobile like '${mobile}%'
and u.login_name = #{loginName}
and u.register_time >= #{beginTime}
and u.register_time <= #{endTime}
and u.referrer = #{referrer}
and u.channel = #{channel}
and u.source = #{source}
order by u.register_time desc limit #{index},#{pageSize}

