SELECT u.*, a.*, referrer_u.`mobile` AS referrer_mobile FROM (user u LEFT JOIN account a ON u.`login_name` = a.`login_name`) LEFT JOIN user referrer_u ON u.`referrer` = referrer_u.`login_name`
AND a.`identity_number` = #{identityNumber}
AND u.`login_name` = #{loginName}
AND referrer_u.`mobile` = #{referrerMobile}
AND u.`mobile` = #{mobile}
ORDER BY u.`register_time` DESC

SELECT `user`.*, `account`.*,
(select `referrer`.`mobile` from `user` `referrer` where `referrer`.`login_name` = `user`.`referrer`) as referrer_mobile,
(select max(`user_bill`.`created_time`) from `user_bill` where `user_bill`.`login_name` = `user`.`login_name`) as last_bill_time
FROM `user`
JOIN `account`
ON `user`.`login_name` = `account`.`login_name`
WHERE NOT EXISTS (SELECT 1 FROM `user_role`  WHERE `user`.`login_name` = `user_role`.`login_name` AND `user_role`.`role` = 'LOANER')

    AND `user`.`mobile` = #{mobile}


    AND `account`.`balance` >= #{balanceMin}


    AND `account`.`balance` <= #{balanceMax}

ORDER BY `account`.`balance` DESC
limit #{startLimit}, #{endLimit}

SELECT IFNULL(SUM(`account`.`balance`), 0)
