mapper.select:
select
    u.*,
    a.*,
    IF(au.login_name is null, "0", "1") as auto_invest_status
from user u
    left join account a on u.login_name = a.login_name


    join user_role r on u.login_name = r.login_name


    left join auto_invest_plan au on u.login_name = au.login_name and enabled=1


        and r.role = #{role}


        and u.email like '${email}%'


        and u.mobile like '${mobile}%'


        and u.login_name = #{loginName}


        and u.register_time >= #{beginTime}


        and u.register_time <= #{endTime}


        and u.referrer = #{referrer}


        and u.channel = #{channel}


        and u.source = #{source}



order by u.register_time desc
limit #{index},#{pageSize}

select count(1) from user u


    join user_role r on u.login_name = r.login_name




        and r.role = #{role}


        and u.email like '${email}%'


        and u.mobile like '${mobile}%'


        and u.login_name = #{loginName}


        and u.register_time >= #{beginTime}


        and u.register_time <= #{endTime}


        and u.referrer = #{referrer}


        and u.channel = #{channel}


        and u.source = #{source}

SELECT
  *
FROM
  user u
  LEFT JOIN account a
    ON u.`login_name` = a.`login_name`


        AND a.`identity_number` = #{identityNumber}


        AND u.`login_name` = #{loginName}


        AND u.`referrer` = #{referrer}


        AND u.`mobile` = #{mobile}


ORDER BY u.`register_time` DESC

SELECT * FROM user u JOIN account a ON u.`login_name` = a.`login_name` AND a.`balance` > 0
AND a.`login_name` = #{loginName}
WHERE NOT EXISTS (SELECT 1 FROM user_role r WHERE u.`login_name` = r.`login_name` AND r.`role` = 'LOANER') ORDER BY a.`balance` DESC limit #{startLimit}, #{endLimit}

SELECT IFNULL(SUM(a.`balance`), 0) FROM user u JOIN account a ON u.`login_name` = a.`login_name` AND a.`balance` > 0
AND a.`login_name` = #{loginName}
WHERE NOT EXISTS (SELECT 1 FROM user_role r WHERE u.`login_name` = r.`login_name` AND r.`role` = 'LOANER')

SELECT IFNULL(count(*), 0) FROM user u JOIN account a ON u.`login_name` = a.`login_name` AND a.`balance` > 0
AND a.`login_name` = #{loginName}
WHERE NOT EXISTS (SELECT 1 FROM user_role r WHERE u.`login_name` = r.`login_name` AND r.`role` = 'LOANER')
