mapper.update:
update withdraw
status = #{status}, verify_time = #{verifyTime}, verify_message = #{verifyMessage}, recheck_time = #{recheckTime}, recheck_message = #{recheckMessage}
where id = #{id}

mapper.select:
select w.id, w.login_name, w.amount, w.fee, w.verify_message, w.verify_time, w.recheck_message, w.recheck_time, w.created_time, w.status, w.source, a.user_name as user_name,
exists (select 1 from user_role r where r.login_name = w.login_name and r.role='admin') as is_admin
from withdraw w join account a on w.login_name = a.login_name


        w.id = #{withdrawId}


        and w.login_name like '${loginName}%'


        and w.created_time >= #{startTime}


        and w.created_time <= #{endTime}


        and w.status  = #{status}


order by w.created_time desc
limit #{index}, #{pageSize}

select count(1) from withdraw


        id = #{withdrawId}


        and login_name like '${loginName}%'


        and created_time >= #{startTime}


        and created_time <= #{endTime}


        and status  = #{status}
