INSERT INTO `node` VALUES ('xinghuicaifu', 'article', '为什么选择 聚融天下 投资？', '', 'c11f07ca68e149148cff6b8e04faaaea', '*', '1', '', '聚融天下互联网投融资平台是联系投资人、借款人的第三方公信力及风险控制创新管理投资服务平台，以第三方账户资金托管和供应链金融方式控制、降低投资风险，以自有风险保证金、风险保证金提留、信用征信机制、担保保本利综合管理保障投资人的利益！', '2014-11-25 16:34:10', '2014-11-25 16:34:10', null, 'admin', 'admin', '0', '');
