INSERT INTO `node_body` VALUES ('9bdeeb4ab6ac45a3a70d4b6da04d955c', '<p><span style=\";font-weight:bold;font-size:14px;font-family:&#39;宋体&#39;\">高收益</span></p><p><span style=\";font-size:14px;font-family:&#39;宋体&#39;\">发发贷在过去的一年里，平均年化收益<span style=\"font-family:Times New Roman\">****</span><span style=\"font-family:宋体\">以上，是银行活期的&nbsp;倍。假设您在一年前就投资发发贷</span><span style=\"font-family:Times New Roman\">10</span><span style=\"font-family:宋体\">万元，那么您降得到的本息为</span><span style=\"font-family:Times New Roman\">****</span><span style=\"font-family:宋体\">以上。</span></span></p><p><span style=\";font-size:14px;font-family:&#39;宋体&#39;\">&nbsp;</span></p><p><span style=\";font-weight:bold;font-size:14px;font-family:&#39;宋体&#39;\">门槛低</span></p><p><span style=\";font-size:14px;font-family:&#39;宋体&#39;\">在发发贷理财，就算您只有</span><span style=\";color:rgb(255,0,0);font-size:14px;font-family:&#39;宋体&#39;\">****</span><span style=\";font-size:14px;font-family:&#39;宋体&#39;\">元，也可轻松享有<span style=\"font-family:Times New Roman\">****</span><span style=\"font-family:宋体\">以上的收益回报。</span></span></p><p><span style=\";font-size:14px;font-family:&#39;宋体&#39;\">&nbsp;</span></p><p><span style=\";font-weight:bold;font-size:14px;font-family:&#39;宋体&#39;\">高效便捷</span></p><p><span style=\";font-size:14px;font-family:&#39;宋体&#39;\">无论您身在何处，都能随时随地的感受发发贷理财的便携性。发发贷官网、发发贷手机站、发发贷公众微信号。</span></p><p><span style=\";font-size:14px;font-family:&#39;宋体&#39;\">&nbsp;</span></p><p><span style=\";font-weight:bold;font-size:14px;font-family:&#39;宋体&#39;\">自由规划</span></p><p><span style=\";font-size:14px;font-family:&#39;宋体&#39;\">我喜欢短期投资，这样资金流动性好；我喜欢长期理财，因为我没时间操作；<span style=\"font-family:Times New Roman\">1</span><span style=\"font-family:宋体\">月、</span><span style=\"font-family:Times New Roman\">2</span><span style=\"font-family:宋体\">个月、</span><span style=\"font-family:Times New Roman\">3</span><span style=\"font-family:宋体\">个月、</span><span style=\"font-family:Times New Roman\">6</span><span style=\"font-family:宋体\">个月、</span><span style=\"font-family:Times New Roman\">1</span><span style=\"font-family:宋体\">年、</span><span style=\"font-family:Times New Roman\">3</span><span style=\"font-family:宋体\">年、自动投标，不同理财期限的标的，满足理财人的不同需求。</span></span></p><p><span style=\";font-size:14px;font-family:&#39;宋体&#39;\">&nbsp;</span></p><p><span style=\";font-weight:bold;font-size:14px;font-family:&#39;宋体&#39;\">对比其他行业理财</span></p><p><span style=\"font-size:14px;font-family:&#39;宋体&#39;\">1、</span><span style=\";font-size:14px;font-family:&#39;宋体&#39;\">银行活期：</span></p><p><span style=\";font-size:14px;font-family:&#39;宋体&#39;\">&nbsp;&nbsp;&nbsp;年化收益<span style=\"font-family:Times New Roman\">0.35%</span><span style=\"font-family:宋体\">、无亏损性、提现随时、银行担保、年化收益较低；</span></span></p><p><span style=\"font-size:14px;font-family:&#39;宋体&#39;\">2、</span><span style=\";font-size:14px;font-family:&#39;宋体&#39;\">余额宝：</span></p><p><span style=\";font-size:14px;font-family:&#39;宋体&#39;\">&nbsp;&nbsp;&nbsp;年化收益<span style=\"font-family:Times New Roman\">4.5-5.5%</span><span style=\"font-family:宋体\">、亏损性很小、提现</span><span style=\"font-family:Times New Roman\">1-3</span><span style=\"font-family:宋体\">天到账、无担保公司、年化收益较低；</span></span></p><p><span style=\"font-size:14px;font-family:&#39;宋体&#39;\">3、</span><span style=\";font-size:14px;font-family:&#39;宋体&#39;\">发发贷：</span></p><p><span style=\";font-size:14px;font-family:&#39;宋体&#39;\">&nbsp;&nbsp;&nbsp;年化收益8%<span style=\"font-family:宋体\">以上、</span><span style=\"font-family:Times New Roman\">100%</span><span style=\"font-family:宋体\">本息保障、提现当天到账、发发贷风险保障金、年化收益较高。</span></span></p><p><br/></p>');
